import React, { Component } from 'react'
import AppHeader from '../app-header'
import SearchPanel from '../search-panel'
import PostStatusFilter from '../post-status-filter'
import PostList from '../post-list'
import PostAddForm from '../post-add-form'
import './app.css'

export default class App extends Component {
    constructor(props)
    {
        super(props);
        this.state={
            data: [
                {label: "Дело номер 1", important: true, id: 'label1', like: false},
                {label: "Дело номер 2", important: false, id: 'label2', like: false},
                {label: "Дело номер 3", important: false, id: 'label3', like: false},
            ]
        };
        this.MaxId=4;
        this.deleteItem =this.deleteItem.bind(this)
        this.addItem =this.addItem.bind(this)
        this.onToggleImportant =this.onToggleImportant.bind(this)
        this.onToggleLiked =this.onToggleLiked.bind(this)
    }
    deleteItem(id) {
        this.setState(({data})=>
        {
            const newArr=data.filter((e)=>e.id!==id)
            return {
                data: newArr
            }
        })
    }
    addItem(body)
    {
        const newItem ={
            label: body,
            id: this.MaxId++,
            important: false,
            like: false

        }
        this.setState(({data})=>
        {
            const newArr=[...data, newItem]
            return {
                data: newArr
            }
        })
    }
    onToggleImportant(id) 
    {
        console.log(`Important ${id}`);
    }
    onToggleLiked(id)
    {
        this.setState(({data})=>
        {
            const likeItem=data.find((e)=>e.id===id)
            likeItem.like=true;
            const newArr=[...data,likeItem]
            return {
                data: newArr
            }
        })
    }
   render () {
        return ( <div className="app">
        <AppHeader/>
        <div className="search-panel d-flex">
        <SearchPanel/>
        <PostStatusFilter/>
        </div>
        <PostList 
        posts={this.state.data}
        onDelete={this.deleteItem}
        onToggleImportant={this.onToggleImportant}
        onToggleLiked={this.onToggleLiked}
        />
        <PostAddForm
        onAdd={this.addItem}
        />

    </div>
)
   }
}
