import React, { Component } from 'react'
import AppHeader from '../app-header'
import SearchPanel from '../search-panel'
import PostStatusFilter from '../post-status-filter'
import PostList from '../post-list'
import PostAddForm from '../post-add-form'
import './app.css'

export default class App extends Component {
    constructor(props)
    {
        super(props);
        this.state={
            data: [
                {label: "Дело номер 1", important: true, like: false, id: 'label1'},
                {label: "Дело номер 2", important: false,  like: false, id: 'label2'},
                {label: "Дело номер 3", important: false,  like: false, id: 'label3'},
            ],
            term: ''
        };
        this.MaxId=4;
        this.deleteItem =this.deleteItem.bind(this)
        this.addItem =this.addItem.bind(this)
        this.onToggleImportant =this.onToggleImportant.bind(this)
        this.onToggleLiked =this.onToggleLiked.bind(this)
    }
    deleteItem(id) {
        this.setState(({data})=>
        {
            const newArr=data.filter((e)=>e.id!==id)
            return {
                data: newArr
            }
        })
    }
    addItem(body)
    {
        const newItem ={
            label: body,
            id: this.MaxId++,
            important: false,
            like: false

        }
        this.setState(({data})=>
        {
            const newArr=[...data, newItem]
            return {
                data: newArr
            }
        })
    }
    onToggleImportant(id) 
    {
        this.setState(({data})=>
        {
            const index=data.findIndex((e)=>e.id===id);
            const old=data[index]
            const newItem={...old,important: !old.important}
            const newArr=[...data.slice(0,index), newItem, ...data.slice(index+1)]
            return {
                data: newArr
            }
        })
    }
    onToggleLiked(id)
    {
        this.setState(({data})=>
        {
            const index=data.findIndex((e)=>e.id===id);
            const old=data[index]
            const newItem={...old,like: !old.like}
            const newArr=[...data.slice(0,index), newItem, ...data.slice(index+1)]
            return {
                data: newArr
            }
        })
    }
    searchPost(items,term)
    {
        if (term.length===0) return items

        return items.filter((item)=>item.label.indexOf(term)>-1)
    }
    onUpdateSearch(term)
    {
        this.setState({term})
    }
   render () {
        const {data,term}=this.state;
        const liked=data.filter((e)=>e.like).length;
        const allPosts=data.length;
        const visiblePosts=this.searchPost(data,term);
        return ( <div className="app">
        <AppHeader
        liked={liked}
        allPosts={allPosts}
        />
        <div className="search-panel d-flex">
        <SearchPanel
        onUpdateSearch={this.onUpdateSearch}
        />
        <PostStatusFilter/>
        </div>
        <PostList 
        posts={visiblePosts}
        onDelete={this.deleteItem}
        onToggleImportant={this.onToggleImportant}
        onToggleLiked={this.onToggleLiked}
        />
        <PostAddForm
        onAdd={this.addItem}
        />

    </div>
)
   }
}
